export {}

declare global {
  namespace google {
    namespace maps {
      interface LatLngLiteral {
        lat: number
        lng: number
      }

      interface MapTypeStyle {
        featureType?: string
        elementType?: string
        stylers?: Array<Record<string, unknown>>
      }

      enum ControlPosition {
        BOTTOM_CENTER,
        BOTTOM_LEFT,
        BOTTOM_RIGHT,
        LEFT_BOTTOM,
        LEFT_CENTER,
        LEFT_TOP,
        RIGHT_BOTTOM,
        RIGHT_CENTER,
        RIGHT_TOP,
        TOP_CENTER,
        TOP_LEFT,
        TOP_RIGHT,
      }

      class MVCArray<T> {
        push(item: T): number
        removeAt(index: number): T
        getArray(): T[]
      }

      interface MapOptions {
        center?: LatLngLiteral
        zoom?: number
        disableDefaultUI?: boolean
        mapId?: string
        styles?: MapTypeStyle[]
        zoomControl?: boolean
        mapTypeControl?: boolean
        fullscreenControl?: boolean
        streetViewControl?: boolean
      }

      class MapsEventListener {
        remove(): void
      }

      class Map {
        constructor(container: HTMLElement, opts?: MapOptions)
        setOptions(options: Partial<MapOptions>): void
        addListener(eventName: string, handler: (...args: any[]) => void): MapsEventListener
        getZoom(): number | undefined
        fitBounds(bounds: LatLngBounds, padding?: number): void
        controls: Record<ControlPosition, MVCArray<HTMLElement>>
        setZoom?(zoom: number): void
      }

      class Polyline {
        constructor(options: any)
        setOptions(options: any): void
        setMap(map: Map | null): void
      }

      class LatLngBounds {
        extend(latLng: LatLngLiteral): void
        isEmpty(): boolean
      }

      interface GeocoderAddressComponent {
        long_name: string
        short_name: string
        types: string[]
      }

      interface GeocoderResult {
        formatted_address?: string
        address_components?: GeocoderAddressComponent[]
      }

      interface GeocoderResponse {
        results?: GeocoderResult[]
      }

      class Geocoder {
        geocode(request: { location: LatLngLiteral }): Promise<GeocoderResponse>
      }

      namespace marker {
        class AdvancedMarkerElement {
          constructor(options: any)
          position: LatLngLiteral
          map: Map | null
          content: HTMLElement
          addListener(eventName: string, handler: (...args: any[]) => void): MapsEventListener
        }
      }

      namespace event {
        function addListenerOnce(instance: any, eventName: string, handler: (...args: any[]) => void): MapsEventListener
        function removeListener(listener: MapsEventListener): void
      }
    }
  }
}
